INSERT INTO productos(nombre,precio,imagen,stock,categoria,descripcion ) VALUES ("ASMODUS COLOSSAL 80W",51.90,"https://morethanvapersshop.es/wp-content/uploads/2018/03/pink-1_large_a403c85b-ff3b-44f1-b62e-df765164023e_large-300x300.jpg",10,"MOD","Asmodus nos presenta su nuevo Mod de una sola batería (No Incluida) dispone de varios modos para desbloquear la pantalla, pudiendo elegir entre deslizar la pantalla hacia abajo o darle tres toques a la pantalla. 
